// Pega o XML do campo correto (no seu caso, vem em $json.data)
const xml = String($json.data || $json.raw_xml || $json.xml || '');

// Helper pra pegar conteÃÂºdo entre tags simples: <tag>valor</tag>
function getTag(tag) {
  const re = new RegExp(`<${tag}>([\\s\\S]*?)<\\/${tag}>`, 'i');
  const m = xml.match(re);
  return m ? String(m[1]).trim() : '';
}

function toInt(v, fallback = 0) {
  const n = parseInt(String(v).replace(/[^\d-]/g, ''), 10);
  return Number.isFinite(n) ? n : fallback;
}

// Extrai campos bÃÂ¡sicos direto do XML
const extracted = {
  current_health: toInt(getTag('currentHealth'), 0),
  max_health: toInt(getTag('maxHealth'), 0),
  current_temp_hp: toInt(getTag('currentTempHP'), 0),
  armor_bonus: toInt(getTag('armorBonus'), 0),
  shield_bonus: toInt(getTag('shieldBonus'), 0),
  base_speed: toInt(getTag('baseSpeed'), 0),

  // Campos Ã¢ÂÂrawÃ¢ÂÂ gigantes (salvar como string)
  ability_scores: getTag('abilityScores'),
  class_data: getTag('classData'),
  weapon_list: getTag('weaponList'),
  note_list: getTag('noteList'),
};

// Metadados (quando nÃÂ£o existirem, tenta pegar do Telegram Trigger)
const trig = $node['Telegram Trigger']?.json ?? {};
const telegram_user_id =
  $json.telegram_user_id ??
  trig?.message?.from?.id ??
  trig?.callback_query?.from?.id ??
  trig?.edited_message?.from?.id ??
  null;

const telegram_username =
  $json.telegram_username ??
  trig?.message?.from?.username ??
  trig?.callback_query?.from?.username ??
  trig?.edited_message?.from?.username ??
  '';

const file_name = $json.file_name ?? '';

return [{
  json: {
    telegram_user_id,
    telegram_username,
    file_name,

    current_health: extracted.current_health,
    max_health: extracted.max_health,
    current_temp_hp: extracted.current_temp_hp,
    armor_bonus: extracted.armor_bonus,
    shield_bonus: extracted.shield_bonus,
    base_speed: extracted.base_speed,

    ability_scores_raw: String(extracted.ability_scores || ''),
    class_data_raw: String(extracted.class_data || ''),
    weapon_list_raw: String(extracted.weapon_list || ''),
    note_list_raw: String(extracted.note_list || ''),

    // XML completo salvo (STRING)
    raw_xml: xml,

    // JSON completo salvo (STRING) Ã¢ÂÂ Data Table aceita
    character_json: JSON.stringify({
      source: 'xml',
      extracted,
      raw_xml: xml,
    }),

    created_at: new Date().toISOString(),
  }
}];

// Divide a mensagem do Agent para envio ao Telegram
// E controla EXPLICITAMENTE quando pode salvar

const full =
  $json.mensagem ??
  $json.output ??
  $json.text ??
  'Desculpe, nÃÂ£o consegui gerar uma resposta.';

const MAX = 3500;
const text = String(full);
const chunks = [];

for (let i = 0; i < text.length; i += MAX) {
  chunks.push(text.slice(i, i + MAX));
}

// Telegram context
const trig = $node['Telegram Trigger']?.json ?? {};

const chatId =
  $json.chat_id ??
  trig?.message?.chat?.id ??
  trig?.callback_query?.message?.chat?.id ??
  trig?.edited_message?.chat?.id;

if (!chatId) {
  throw new Error('chat_id ausente para responder no Telegram.');
}

// Telegram user info (fallback seguro)
const telegram_user_id =
  $json.telegram_user_id ??
  trig?.message?.from?.id ??
  trig?.callback_query?.from?.id ??
  trig?.edited_message?.from?.id ??
  null;

const telegram_username =
  $json.telegram_username ??
  trig?.message?.from?.username ??
  trig?.callback_query?.from?.username ??
  trig?.edited_message?.from?.username ??
  '';

// Ã°ÂÂÂ¨ REGRA DE OURO:
// NUNCA inferir salvamento
// SÃÂ³ salva se o AGENT marcar explicitamente
const deve_salvar = ($json.deve_salvar === true);
const pronto_para_salvar = ($json.pronto_para_salvar === true);

// Payload base
const basePayload = {
  chat_id: chatId,
  telegram_user_id,
  telegram_username,
  deve_salvar,
  pronto_para_salvar,
};

// Envia mensagens (sempre)
return chunks.map(chunk => ({
  json: {
    ...basePayload,
    text: chunk,
  }
}));
