return items.map(item => {
  const file = item.binary?.data;

  if (!file || !file.data) {
    throw new Error('Nenhum arquivo recebido em item.binary.data');
  }

  const buffer = Buffer.from(file.data, file.encoding || 'base64');
  let xmlText = buffer.toString('utf8');

  xmlText = xmlText.replace(/^﻿/, '');
  xmlText = xmlText.replace(/[�-  -]/g, '');
  xmlText = xmlText.replace(/&(?!amp;|lt;|gt;|quot;|apos;|#\d+;|#x[0-9a-fA-F]+;)/g, '&amp;');

  const msg = item.json.message || {};

  item.json = {
    telegram_user_id: msg.from?.id,
    telegram_username: msg.from?.username || '',
    file_name: msg.document?.file_name || '',
    xml_text: xmlText,
    raw_xml: xmlText,
    mensagem: '✅ Ficha importada com sucesso!'
  };

  return item;
});

const char = $json.character || {};
const meta = $node['Ler Arquivo XML do JSON'].json || {};

return [{
  json: {
    telegram_user_id: meta.telegram_user_id || null,
    telegram_username: meta.telegram_username || '',
    file_name: meta.file_name || '',
    current_health: Number(char.currentHealth || 0),
    max_health: Number(char.maxHealth || 0),
    current_temp_hp: Number(char.currentTempHP || 0),
    armor_bonus: Number(char.armorBonus || 0),
    shield_bonus: Number(char.shieldBonus || 0),
    base_speed: Number(char.baseSpeed || 0),
    ability_scores_raw: char.abilityScores || '',
    class_data_raw: char.classData || '',
    weapon_list_raw: char.weaponList || '',
    note_list_raw: char.noteList || '',
    character_json: char,
    raw_xml: meta.raw_xml || '',
    mensagem: '✅ Ficha importada e salva com sucesso!'
  }
}];

const full = $json.mensagem || $json.output || 'Desculpe, ocorreu um erro.';
const MAX = 3500;
const text = String(full);
const chunks = [];
for (let i = 0; i < text.length; i += MAX) {
  chunks.push(text.slice(i, i + MAX));
}

const chatId = $node['Telegram Trigger'].json.message.chat.id;
const deveSalvar = $json.deve_salvar === true;

const basePayload = {
  chat_id: chatId,
  deve_salvar: deveSalvar,
  file_name: $json.file_name || null,
  current_health: Number($json.current_health || 0),
  max_health: Number($json.max_health || 0),
  current_temp_hp: Number($json.current_temp_hp || 0),
  armor_bonus: Number($json.armor_bonus || 0),
  shield_bonus: Number($json.shield_bonus || 0),
  base_speed: Number($json.base_speed || 30),
  ability_scores_raw: $json.ability_scores_raw || '',
  class_data_raw: $json.class_data_raw || '',
  weapon_list_raw: $json.weapon_list_raw || '',
  note_list_raw: $json.note_list_raw || '',
  character_json: $json.character_json || {},
  raw_xml: $json.raw_xml || ''
};

const hasCharacter = basePayload.character_json && Object.keys(basePayload.character_json).length > 0;
const requiredStrings = ['file_name', 'ability_scores_raw', 'class_data_raw'];
const hasRequiredStrings = requiredStrings.every((key) => {
  const value = basePayload[key];
  return typeof value === 'string' ? value.trim().length > 0 : Boolean(value);
});
const readyToSave = deveSalvar && hasCharacter && hasRequiredStrings;

if (deveSalvar && !readyToSave) {
  basePayload.validation_error = 'Faltam dados essenciais da ficha (nome, atributos, classe ou detalhes). Confirme todas as etapas antes de salvar.';
}

basePayload.pronto_para_salvar = readyToSave;

return chunks.map((chunk) => ({
  json: {
    ...basePayload,
    text: chunk
  }
}));

