{
  "Barbarian": {
    "name_pt": "Bárbaro",

    "overview_text": "Aventureiros ferozes definidos por sua fúria – desenfreada, inextinguível e irracional –, que canalizam instintos primitivos, resistência física e proeza em combate. Para alguns, a fúria nasce da comunhão com espíritos animais; para outros, de um reservatório emocional de dor e raiva. Bárbaros se sentem mais vivos no caos da batalha e atuam como protetores tribais ou líderes em tempos de guerra.",

    "instinto_primitivo": "Bárbaros rejeitam a civilização como sinal de fraqueza e abraçam seus instintos selvagens. Crescem em ambientes hostis como tundras, selvas e pradarias, sentindo-se desconfortáveis em cidades e cercados por multidões.",

    "vida_de_perigo": "Vivem cercados por ameaças constantes: tribos rivais, clima mortal e monstros. Enfrentam o perigo de frente para proteger seu povo e frequentemente tornam-se aventureiros por necessidade ou dever.",

    "criando_um_barbaro": {
      "roleplay_guidance": [
        "Pense sobre sua origem tribal ou selvagem.",
        "Defina se você veio de terras distantes ou regiões fronteiriças.",
        "Determine o evento que o levou ao caminho da aventura: guerras, invasões, prisão, banimento ou desejo de riqueza."
      ],
      "construcao_rapida": [
        "Priorize Força.",
        "Segundo maior valor em Constituição.",
        "Escolha o antecedente Forasteiro."
      ]
    },

    "hit_die": "d12",

    "hp_rules": {
      "level_1": "12 + modificador de Constituição",
      "next_levels": "1d12 (ou 7) + modificador de Constituição por nível"
    },

    "proficiencies": {
      "armor": ["Armaduras leves", "Armaduras médias", "Escudos"],
      "weapons": ["Armas simples", "Armas marciais"],
      "tools": [],
      "saving_throws": ["Força", "Constituição"],
      "skill_choices": {
        "count": 2,
        "options": [
          "Adestrar Animais",
          "Atletismo",
          "Intimidação",
          "Natureza",
          "Percepção",
          "Sobrevivência"
        ]
      }
    },

    "starting_equipment": [
      {
        "choice_id": 1,
        "options": [
          { "id": "A", "items": ["Machado grande"] },
          { "id": "B", "items": ["Qualquer arma marcial corpo-a-corpo"] }
        ]
      },
      {
        "choice_id": 2,
        "options": [
          { "id": "A", "items": ["Dois machados de mão"] },
          { "id": "B", "items": ["Qualquer arma simples"] }
        ]
      },
      {
        "choice_id": 3,
        "options": [
          { "id": "A", "items": ["Pacote de aventureiro"] }
        ]
      },
      {
        "fixed_items": ["Quatro azagaias"]
      }
    ],

    "features": {
      "level_1": [
        {
          "name": "Fúria",
          "description": "Ação bônus para entrar em fúria por 1 minuto. Benefícios: vantagem em testes de Força e resistências de Força; bônus de dano em ataques corpo-a-corpo com Força conforme a tabela; resistência a danos concussão, cortante e perfurante. Não pode conjurar ou manter concentração enquanto estiver em fúria."
        },
        {
          "name": "Defesa sem Armadura",
          "description": "CA = 10 + modificador de Destreza + modificador de Constituição quando não estiver usando armadura. Pode usar escudo."
        }
      ],

      "level_2": [
        {
          "name": "Ataque Descuidado",
          "description": "Concede vantagem em ataques corpo-a-corpo com Força no turno, mas concede vantagem contra você até seu próximo turno."
        },
        {
          "name": "Sentido de Perigo",
          "description": "Vantagem em testes de resistência de Destreza contra efeitos visíveis."
        }
      ],

      "level_3": [
        {
          "name": "Caminho Primitivo",
          "description": "Escolha: Caminho do Furioso ou Caminho do Guerreiro Totêmico."
        }
      ],

      "level_4_8_12_16_19": [
        {
          "name": "Incremento de Atributo",
          "description": "Aumentar dois atributos em +1 ou um atributo em +2, máximo padrão 20."
        }
      ],

      "level_5": [
        {
          "name": "Ataque Extra",
          "description": "Atacar duas vezes com a ação de Ataque."
        },
        {
          "name": "Movimento Rápido",
          "description": "+3 metros de deslocamento se não usar armadura pesada."
        }
      ],

      "level_7": [
        {
          "name": "Instinto Selvagem",
          "description": "Vantagem nas iniciativas e pode agir mesmo surpreso se entrar em fúria."
        }
      ],

      "level_9_13_17": [
        {
          "name": "Crítico Brutal",
          "description": "Rola dados extras ao causar crítico: +1 dado no 9°, +2 dados no 13°, +3 dados no 17°."
        }
      ],

      "level_11": [
        {
          "name": "Fúria Implacável",
          "description": "Em 0 PV enquanto em fúria, teste de CON CD 10 para voltar a 1 PV; CD aumenta em +5 para cada uso até descanso."
        }
      ],

      "level_15": [
        {
          "name": "Fúria Persistente",
          "description": "Fúria só termina se cair inconsciente ou se decidir encerrá-la."
        }
      ],

      "level_18": [
        {
          "name": "Força Indomável",
          "description": "Se um teste de Força for menor que seu valor de Força, usa o valor fixo."
        }
      ],

      "level_20": [
        {
          "name": "Campeão Primitivo",
          "description": "Força e Constituição aumentam em +4. Máximo passa a ser 24."
        }
      ]
    },

    "progression_table": [
